package LANraragi::Plugin::Metadata::Date;

use strict;
use warnings;
use utf8;

#Plugins can freely use all Perl packages already installed on the system
#Try however to restrain yourself to the ones already installed for LRR (see tools/cpanfile) to avoid extra installations by the end-user.
#基于date_added插件修改，用于添加中文时间标签
use Mojo::UserAgent;

use LANraragi::Model::Plugins;
use LANraragi::Utils::Logging qw(get_logger);

#Meta-information about your plugin.
sub plugin_info {

    return (
        #Standard metadata
        name        => "Date",
        type        => "metadata",
        namespace   => "date",
        author      => "dezhao",
        version     => "0.1",
        description => "添加时间戳 \"时间\" 命名空间，以相对1970年1月1日起以秒计数.",
        icon =>
          "data:image/png;base64,",
        parameters => [ { type => "bool", desc => "使用文件修改时间而不是当前时间." } ],
        oneshot_arg =>
          "使用文件修改时间（yes/true），或使用当前时间（no/false）。 <br/>留空使用全局设置（默认：当前时间）"
    );

}

#Mandatory function to be implemented by your plugin
sub get_tags {

    shift;
    my $lrr_info = shift;       # Global info hash
    my ($use_filetime) = @_;    # Plugin parameters

    #Use the logger to output status - they'll be passed to a specialized logfile and written to STDOUT.
    my $logger = get_logger( "date", "plugins" );

    #Work your magic here - You can create subroutines below to organize the code better

    $logger->debug( "处理文件: " . $lrr_info->{file_path} );
    my $newtags              = "";
    my $oneshotarg           = $lrr_info->{oneshot_param};
    my $oneshot_file_time    = $oneshotarg =~ /^(yes|true)$/i;
    my $oneshot_current_time = $oneshotarg =~ /^(no|false)$/i;

    if ( $oneshot_file_time || ( $use_filetime && !$oneshot_current_time ) ) {
        $logger->info("使用文件时间");
        $newtags = "时间(秒):" . ( stat( $lrr_info->{file_path} ) )[9];    #9 is the unix time stamp for date modified.
    } else {
        $logger->info("使用当前时间");
        $newtags = "时间(秒):" . time();
    }
    return ( tags => $newtags );
}

1;

